$(document).ready(function(){$window=$(window),$("[data-type]").each(function(){$(this).data("offsetY",parseInt($(this).attr("data-offsetY"))),$(this).data("Xposition",$(this).attr("data-Xposition")),$(this).data("speed",$(this).attr("data-speed"))}),$('.work_object[data-type="background"]').each(function(){var t=$(this),a=t.offset(),o=a.top;$(window).scroll(function(){if($window.scrollTop()+$window.height()>o&&o+t.height()>$window.scrollTop()){var a=-($window.scrollTop()/t.data("speed"));t.data("offsetY")&&(a+=t.data("offsetY"));var s="50% "+a+"px";t.css({backgroundPosition:s}),$('[data-type="sprite"]',t).each(function(){var t=$(this),a=-($window.scrollTop()/t.data("speed")),o=t.data("Xposition")+" "+(a+t.data("offsetY"))+"px";t.css({backgroundPosition:o})}),$('[data-type="video"]',t).each(function(){var t=$(this),a=-($window.scrollTop()/t.data("speed")),o=a+t.data("offsetY")+"px";t.css({top:o})})}})})});